import React from "react";
import { useLocation, Link } from "react-router-dom";

export default function PaintingPage(){
    const location = useLocation();
    const state = location.state;

    if(state.Continent === 'Europe'){
        const images = require.context('../../Paintings/Europe-paintings', true);
    }
    else if(state.Continent === 'Asia'){
        const images = require.context('../../Paintings/Asia-paintings', true);
    }
    else if(state.Continent === 'America'){
        const images = require.context('../../Paintings/America-paintings', true);
    }

    let imgsrc = images(`./${state.paintingsrc}.jpg`);

    return(
        <>
            <h1> {state.title} </h1>
            <p> {state.text} </p>
            <img src={imgsrc} className="image"/>
        </>
    );
}
